keys.page.chat_bot.commands.examples.song.cod:
if (Music.activePlayback) {
    const title = Music.activePlayback.currentTrack.title;
    const artists = Music.activePlayback.currentTrack.artists.join(", ");
    const link= Music.activePlayback.currentTrack.link;
    Koi.sendChat(event.streamer.platform, `Joue actuellement : ${title} par ${artists} - ${link}`, "SYSTEM", event.id);
} else {
    Koi.sendChat(event.streamer.platform, `Pas de musique en cours.`, "SYSTEM", event.id);
}
