if (Music.activePlayback) {
    const title = Music.activePlayback.currentTrack.title;
    const artists = Music.activePlayback.currentTrack.artists.join(", ");
    const link= Music.activePlayback.currentTrack.link;
    Koi.sendChat(event.streamer.platform, `Joue actuellement : ${title} par ${artists} - ${link}`, "SYSTEM", event.id);
} else {
    Koi.sendChat(event.streamer.platform, `Pas de musique en cours.`, "SYSTEM", event.id);
}

page.chat_bot.commands.examples.where.co:
const listOfLinks = [];

for (const state of Object.values(Koi.streamStates)) {
    if (state.is_live) {
        listOfLinks.push(state.streamer.link);
}
}

 // Nous ne sommes en direct nulle part, il suffit de ne pas envoyer tous les liens.
if (listOfLinks.length == 0) {
    for (const state of Object.values(Koi.userStates)) {
        listOfLinks.push(state.streamer.link);
    }

    Koi.sendChat(event.streamer.platform, `Nous streamons √† : ${listOfLinks.join(", ")}`, "SYSTEM", event.id);
    return;
}

Koi.sendChat(event.streamer.platform, `Nous sommes actuellement en direct : ${listOfLinks.join(", ")}`, "SYSTEM", event.id);
