page.chat_bot.commands.examples.shoutout:
if (args.length == 0) {
    Koi.sendChat(event.streamer.platform, `Du musst einen Streamer auswählen für dein shoutout!`, "SYSTEM", event.id);
} else {
    Koi.sendChat(event.streamer.platform, `Shoutout an @${args[0]}!`, "SYSTEM", null);
}

page.chat_bot.commands.examples.song.cod:
if (Music.activePlayback) {
    const title = Music.activePlayback.currentTrack.title;
    const artists = Music.activePlayback.currentTrack.artists.join(", ");
    const link= Music.activePlayback.currentTrack.link;
    Koi.sendChat(event.streamer.platform, `Jetzt spielt: ${title} von ${artists} - ${link}`, "SYSTEM", event.id);
} else {
    Koi.sendChat(event.streamer.platform, `Gerade wird kein Song abgespielt.`, "SYSTEM", event.id);
}
