page.chat_bot.commands.examples.shoutout:
if (args.length == 0) {
    Koi.sendChat(event.streamer.platform, `Anda harus menyebut secara detail streamer yang akan di shout out!`, "SYSTEM", event.id);
} else {
    Koi.sendChat(event.streamer.platform, `Semuanya! Lihat sekarang @${args[0]}!`, "SYSTEM", null);
}

page.chat_bot.commands.examples.song.cod:
if (Music.activePlayback) {
    const title = Music.activePlayback.currentTrack.title;
    const artists = Music.activePlayback.currentTrack.artists.join(", ");
    const link= Music.activePlayback.currentTrack.link;
    Koi.sendChat(event.streamer.platform, `Sedang diputar : ${title} dinyanyikan oleh ${artists} - ${link}`, "SYSTEM", event.id);
} else {
    Koi.sendChat(event.streamer.platform, `Tidak ada lagu yang sedang di putar.`, "SYSTEM", event.id);
}
