page.chat_bot.commands.examples.shoutout:
if (args.length == 0) {
    Koi.sendChat(event.streamer.platform, `You must specify a streamer to shout out!`, "SYSTEM", event.id);
} else {
    Koi.sendChat(event.streamer.platform, `Everybody go check out @${args[0]}!`, "SYSTEM", null);
}

page.chat_bot.commands.examples.song.cod:
if (Music.activePlayback) {
    const title = Music.activePlayback.currentTrack.title;
    const artists = Music.activePlayback.currentTrack.artists.join(", ");
    const link= Music.activePlayback.currentTrack.link;
    Koi.sendChat(event.streamer.platform, `Now playing: ${title} by ${artists} - ${link}`, "SYSTEM", event.id);
} else {
    Koi.sendChat(event.streamer.platform, `No song is playing.`, "SYSTEM", event.id);
}

page.chat_bot.commands.examples.where.co:
const listOfLinks = [];

for (const state of Object.values(Koi.streamStates)) {
    if (state.is_live) {
        listOfLinks.push(state.streamer.link);
}
}

 // We're not live anywhere, just dump all of the links.
if (listOfLinks.length == 0) {
    for (const state of Object.values(Koi.userStates)) {
        listOfLinks.push(state.streamer.link);
    }

    Koi.sendChat(event.streamer.platform, `We stream at: ${listOfLinks.join(", ")}`, "SYSTEM", event.id);
    return;
}

Koi.sendChat(event.streamer.platform, `We're currently live at: ${listOfLinks.join(", ")}`, "SYSTEM", event.id);
